import { useState } from "react";
import { SpinnerLoading } from "../../Utils/SpinnerLoading";
import { App, SongList } from "./SongList";
let button = false;

function SwitchPage() {

  return(
<div>
<button onClick={handleButtonClick}>Upload Song</button>
</div>
  );
}

function handleButtonClick(e) 
{
  button = true;
}

export function ControlPanel() {
  const [refresh, setRefresh] = useState(false);
  const [newSong, setNewSong] = useState(false); 
  if(refresh == false)
  {
    return (
      <>
      <SongList/>
      <FileUpload setRefresh = {setRefresh}/>
      <Refresh/>
      </>
    );
  }
  else
  {
    return (
      <>
        <SpinnerLoading/>
        <FileUpload/>
        <Refresh/>
      </>
    );
  }


  function Refresh() {

  return(
  <div>
  <button onClick={handleButtonClick}>Refresh</button>
  </div>
    );
  }

  function SwitchToSongs() {

    return(
  <div>
  <button onClick={handleButtonClick}>Back to Playlists</button>
  </div>
    );
  }
  
  async function handleButtonClick(e) 
  {
    const delay = ms => new Promise(res => setTimeout(res, ms));

    setRefresh(true);
    await delay(500);
    setRefresh(false);
  }

  
  
}
