import { updateExpressionStatement } from "typescript";
import FileUpload from "../../../FileUpload";
import { App, SongList } from "./SongList";
let button = false;

function SwitchPage() {

  return(
<div>
<button onClick={handleButtonClick}>Upload Song</button>
</div>
  );
}

function handleButtonClick(e) 
{
  button = true;
}

export function ControlPanel() {
  const [page, setPage] = useState(false);

  if(refresh == true)
  {
    return (
      <>
      <SongList/>
      <FileUpload/>

      <Refresh/>
      </>
    );
  }
  return (
    <>
          <FileUpload/>
    <SwitchToSongs/>

    </>
  );

  function Refresh() {

    return(
  <div>
  <button onClick={handleButtonClick}>Upload Song</button>
  </div>
    );
  }

  function SwitchToSongs() {

    return(
  <div>
  <button onClick={handleButtonClick}>Back to Playlists</button>
  </div>
    );
  }
  
  function handleButtonClick(e) 
  {
    if(refresh === true)
    setRefresh(false);
  }
  
}
