import { initializeApp } from "firebase/app";
import { useEffect, useState } from "react";
import { uploadFiles } from "../../../fileUploadUtils";
import { SpinnerLoading } from "../../Utils/SpinnerLoading";
import { StatusBar } from "./Footer/StatusBar";
import { Sound, getCurrentSongName, start, pause, play, restart } from "./Sound";

export function SongList() {
  const [songName, setSongName] = useState([]);
  const [artistName, setArtistName] = useState('');
  const [songsList, setSongsList] = useState([]);
  const [songs, setSongs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [progress, setProgress] = useState(0);

  useEffect(() => {fetch("http://localhost:8080/song/getAll")
  //useEffect(() => {fetch("https://alexsherrowspotify.herokuapp.com/song/getAll")
  .then(res=>res.json())
  .then((result)=>{
    result = JSON.stringify(result);
    result = JSON.parse(result);
    const loadedSongs = [];

    for(const key in result)
    {
      loadedSongs.push({
        id: result[key].id,
        name: result[key].name,
        artist: result[key].artist,
        url: result[key].url
      });
    }
    loadedSongs.forEach((song,index)=>{
    songs.push(<Sound key = {song.id} path = {song.url} name = {song.name} artist = {song.artist} setSongName = {setSongName} setArtistName = {setArtistName}/>)
    })
    setSongsList(songs);
    setIsLoading(false);
  }
)
},[songsList]);

  if (isLoading) {
    return (
      <SpinnerLoading/>
    )
  }
    return (
    <>
    {songsList}
    <div>
    <StatusBar
    songName={songName} 
    artistName={artistName}/>
    <PreviousButton setSongName = {setSongName} setArtistName = {setArtistName}/>
    <PauseButton/>
    <PlayButton/>
    <NextButton setSongName = {setSongName} setArtistName = {setArtistName}/>
    </div>
    <div>
    <FileUpload/>
    </div>
    </>
    );

     function NextButton() {
      return <button onClick={handleNextButtonClick}>Next</button>;
    }
    
    function handleNextButtonClick() {
      getNextSong();
    }

    function PauseButton() {
      return (
          <button onClick={handlePauseButton}>Pause</button>
      );
    }
    
    function handlePauseButton() {
          pause();
    }

    function PlayButton() {
      return (
          <button onClick={handlePlayButton}>Play</button>
      );
    }
    
    function handlePlayButton() {
        play();
        initializeApp();
    }

    function PreviousButton() {
      return <button onClick={handlePreviousButtonClick}>Prev</button>;
    }
    
    function handlePreviousButtonClick() {
      getPreviousSong();
    }

    function getNextSong() 
{
  for(let i = 0; i < songs.length; i++)
  {
    if(songs[i].props.name === getCurrentSongName())
    {
      restart();
      if(i === songs.length - 1)
      start(songs[0].props);
      else
      start(songs[i + 1].props);
      break;
    }
  }
}

function getPreviousSong() 
{
  for(let i = 0; i < songs.length; i++)
  {
    if(songs[i].props.name === getCurrentSongName())
    {
      restart();
      if(i === 0)
      start(songs[songs.length - 1].props);
      else
      start(songs[i - 1].props);
      break;
    }
  }
}

function FileUpload() {
  let file;
  let name;
  let artist;
  const formHandler = (e) => {
      e.preventDefault();
      file = e.target[0].files[0];
      name = e.target[1].value;
      artist = e.target[2].value;
      uploadFiles(file, name, artist, setProgress)
      };
      
      songs.push(<Sound key = {5} path = {file} name = {name} artist = {artist} setSongName = {setSongName} setArtistName = {setArtistName}/>);
      console.log('we are here');
      setSongsList(songs);
      console.log(songs);
    return(
      <div className="App">
          <form onSubmit={formHandler}>
              <input type="file" className="file"/>
              <input type="text" name="songName" defaultValue='Song Name'/>
              <input type="text" name="artistName" defaultValue='Artist Name'/>
              <button type="submit">Upload</button>
          </form>
          <hr/>
          <h3>Uploaded {progress} %</h3>
      </div>
    );

}
};
